import React, { Component } from 'react';
import ReactDOM from 'react-dom';

const users = [
  { username: 'Jerry', age: 21, gender: 'male' },
  { username: 'Tomy', age: 22, gender: 'male' },
  { username: 'Lily', age: 19, gender: 'female' },
  { username: 'Lucy', age: 20, gender: 'female' }
]

class User extends Component {
  render () {
    const { user } = this.props
    return (
      <div>
        <div>ÂßìÂêçÔºö{user.username}</div>
        <div>Âπ¥ÈæÑÔºö{user.age}</div>
        <div>ÊÄßÂà´Ôºö{user.gender}</div>
        <hr />
      </div>
    )
  }
}

class LikeButton extends Component {
  // propsÈªòËÆ§ÈÖçÁΩÆ
  static defaultProps = {
    likedText: 'ÂèñÊ∂à',
    unlikedText: 'ÁÇπËµû'
  }

  constructor () {
    super()
    this.state = { isLiked: false } // isLiked Â≠òÊîæÂú®ÂÆû‰æãÁöÑ state ÂØπË±°ÂΩì‰∏≠
  }

  handleClickOnLikeButton () {
    this.setState({
      isLiked: !this.state.isLiked
    })
    if (this.props.onClick) {
      this.props.onClick()
    }
  }

  render () {
    const wordings = this.props.wordings || {
      likedText: 'ÂèñÊ∂à',
      unlikedText: 'ÁÇπËµû'
    }
    return (
      <button onClick={this.handleClickOnLikeButton.bind(this)}>
        {this.state.isLiked ? wordings.likedText : wordings.unlikedText} üëç
      </button>
    )
    // return (
    //   <button onClick={this.handleClickOnLikeButton.bind(this)}>
    //     {this.state.isLiked ? 'ÂèñÊ∂à' : 'ÁÇπËµû'} üëç
    //   </button>
    // )
  }
}

class Title extends Component {
  handleClickOnTitle (word, e) {
    console.log(this, word)
    console.log(e.target.innerHTML)
    console.log('Click on title.')
  }

  render () {
    return (
      <h1 onClick={this.handleClickOnTitle.bind(this, 'hello')}>React Â∞è‰π¶</h1>
    )
  }
}

class Header extends Component {
  render () {
    return (
    <div>
      <Title />
      <h2>This is Header</h2>
    </div>
    )
  }
}

class Main extends Component {
  render () {
    return (
    <div>
      <h2>This is main content</h2>
    </div>
    )
  }
}

class Footer extends Component {
  render () {
    return (
    <div>
      <h2>This is footer</h2>
    </div>
    )
  }
}

class Index extends Component {
  render () {
    return (
      <div>
        <Header />
        {users.map((user, i) => <User user={user} key={i} />)}
        <LikeButton
          wordings={{likedText: 'Â∑≤Ëµû', unlikedText: 'Ëµû'}}
          onClick={() => console.log('Click on like button!')}/>
        <Main />
        <Footer />
      </div>
    )
  }
}

ReactDOM.render(
  <Index />,
  document.getElementById('root')
)
